def number_handles(handles)
    number_of_handles = handles.length
    puts "Il y a #{number_of_handles} handles dans cette array."
  end
  puts "-"*10
  number_handles(handles)

def find_shortest_handle(handles)
    handles.min_by { |handles| handles.length }
  end
  puts "-"*10
  shortest_handle = find_shortest_handle(handles)
  puts "Le handle le plus court est : #{shortest_handle}"
  
def find_5_caracter
    handles.count { |handles| handles.length - 1 == 5 }  
end
puts "-"*10
caracter_5 = find_5_caracter
puts "Il y a #{caracter_5} handles contenant 5 caractères dans cette array."

def capital_letter
number_capital_letter = handles.count { |handles| handles.match?(/^@[A-Z]/) }  
end
puts "-"*10
number_capital_letter =capital_letter
puts "Il y a #{number_capital_letter} handles commençant par une majuscule dans cette array."

def alphabetical_order(handles)
    sorted_handles = handles.sort_by {|handles| handles.downcase }
    puts sorted_handles
end

def sort_handles_by_size(handles)
  size_handles = handles.sort_by { |handles| handles.length }
  puts size_handles
end

def find_position(handles)
    puts "la position de @epenser est #{handles.index ("@epenser")}"
end

def handle_length_distribution(handles)

    length_distribution = Hash.new(0)
    
    handles.each do |handle|
      length = handle.length - 1  
      length_distribution[length] += 1
    end
    puts "Répartition des handles par taille :"
    length_distribution.each do |length, count|
      puts "#{length + 1} caractère(s): #{count} handle(s)"
    end
  end

  

def execute
    puts "-"*10
    alphabetical_order(handles)
    puts "-"*10
    sort_handles_by_size(handles)
    puts "-"*10
    find_position(handles)
    puts "-"*10
    handle_length_distribution(handles)
end

execute